let MenuControl=document.querySelector(".menu-control"),MENU=document.getElementById("main-menu-box"),date=(MenuControl.addEventListener("click",function(t){t.target.classList.contains("fa-align-right")?(setTimeout(()=>t.target.parentElement.classList.add("switch"),100),MENU.style.transform="translateX(0%)"):(setTimeout(()=>t.target.parentElement.classList.remove("switch"),100),MENU.style.transform="translateX(300%)")}),MENU.addEventListener("click",function(t){"A"===t.target.tagName&&(t.target.parentElement.parentElement.parentElement.style.transform="translateX(300%)",MenuControl.classList.remove("switch"))}),window.addEventListener("click",function(t){!0!==t.target.classList.contains("fa-align-right")&&(MENU.style.transform="translateX(300%)",MenuControl.classList.remove("switch"))}),document.getElementById("current-year")),DATE=new Date;date.innerHTML=DATE.getFullYear();